svg.script:
const symbols = [
  document.getElementById("delta-main"),
  document.getElementById("delta-alt"),
  document.getElementById("delta-greek")
];
let current = 0;

function showNextSymbol() {
  const next = (current + 1) % symbols.length;

  const mainTime = 7000 + Math.random()*2000; // 7-9s Verweildauer
  const fadeTime = 2000 + Math.random()*500;  // 2-2,5s Überblendung

  symbols[next].style.opacity = 0;

  symbols[current].style.transition = `opacity ${fadeTime}ms linear`;
  symbols[next].style.transition = `opacity ${fadeTime}ms linear`;

  setTimeout(() => {
    symbols[current].style.opacity = 0;
    symbols[next].style.opacity = 1;
    current = next;
    showNextSymbol();
  }, mainTime);

  // Sehr langsamer Puls (~12 Schläge/min)
  const scaleMin = 0.999;
  const scaleMax = 1.001;
  const yMin = -0.1;
  const yMax = 0.1;

  const baseColor = [216,192,136];
  const pulseColor = [218,194,138];

  let start = null;
  function pulse(timestamp) {
    if (!start) start = timestamp;
    const elapsed = timestamp - start;
    const t = (elapsed % 5000)/5000; // 5s Zyklus
    const scale = scaleMin + (scaleMax - scaleMin) * Math.sin(t * Math.PI*2);
    const y = yMin + (yMax - yMin) * Math.sin(t * Math.PI*2);
    symbols[current].setAttribute("transform", `translate(0,${y}) scale(${scale})`);
    const r = Math.round(baseColor[0] + (pulseColor[0]-baseColor[0])*Math.sin(t* Math.PI*2));
    const g = Math.round(baseColor[1] + (pulseColor[1]-baseColor[1])*Math.sin(t* Math.PI*2));
    const b = Math.round(baseColor[2] + (pulseColor[2]-baseColor[2])*Math.sin(t* Math.PI*2));
    symbols[current].setAttribute("fill", `rgb(${r},${g},${b})`);
    requestAnimationFrame(pulse);
  }
  requestAnimationFrame(pulse);
}

showNextSymbol();
